import React, { useState } from 'react';
import './Toggle.css'; // You can create a separate CSS file for styling

function Toggle() {
  const [isToggled, setToggled] = useState(false);

  const handleToggle = () => {
    setToggled(!isToggled);
  };

  return (
    <div className={`toggle-container ${isToggled ? 'toggled' : ''}`}>
      <input type="checkbox" id="toggleButton" checked={isToggled} onChange={handleToggle} />
      <label htmlFor="toggleButton" className="toggle-label"></label>
    </div>
  );
}

export default Toggle;
