import React, { useRef } from 'react';

function App() {
  const roleRefs = {
    role1: useRef(null),
    role2: useRef(null),

  };

  const scrollToRole = (role) => {
    const targetRef = roleRefs[role];
    if (targetRef.current) {
      targetRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <div>
      <div>
        <button onClick={() => scrollToRole('role1')}>Role 1</button>
        <button onClick={() => scrollToRole('role2')}>Role 2</button>
        {/* ... other role buttons */}
      </div>
      <div style={{display:'fle'}}>
      <div style={{height:'100vh'}}ref={roleRefs.role1}>
        <h2>Role 1 Section</h2>
        {/* Content for role 1 */}
      </div>
      <div style={{height:'100vh'}} ref={roleRefs.role2}>
        <h2>Role 2 Section</h2>
        {/* Content for role 2 */}
      </div>
      </div>

      {/* ... other role sections */}
    </div>
  );
}

export default App;
